// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

interface ITRC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

/**
 * @title TronEscrowLite
 * @dev Ultra-lightweight HTLC escrow for minimal energy usage
 */
contract TronEscrowLite {

    address public maker;
    address public taker;
    address public token;
    uint256 public amount;
    bytes32 public hashlock;
    uint256 public withdrawStart;
    uint256 public cancelStart;
    uint256 public safetyDeposit;
    bool public withdrawn;
    bool public cancelled;

    uint256 constant MIN_DEPOSIT = 1000000; // 1 TRX

    event Created(address maker, address taker);
    event Withdrawn(bytes32 secret);
    event Cancelled();

    constructor(
        address _taker,
        address _token,
        uint256 _amount,
        bytes32 _hashlock,
        uint256 _withdrawDelay,
        uint256 _cancelDelay
    ) payable {
        require(msg.value >= MIN_DEPOSIT, "Need 1+ TRX deposit");
        require(_cancelDelay > _withdrawDelay, "Bad timing");

        maker = msg.sender;
        taker = _taker;
        token = _token;
        amount = _amount;
        hashlock = _hashlock;
        withdrawStart = block.timestamp + _withdrawDelay;
        cancelStart = block.timestamp + _cancelDelay;
        safetyDeposit = msg.value;

        // Transfer tokens to this contract
        ITRC20(_token).transferFrom(msg.sender, address(this), _amount);

        emit Created(maker, taker);
    }

    function withdraw(bytes32 secret) external {
        require(!withdrawn && !cancelled, "Done");
        require(msg.sender == taker, "Not taker");
        require(block.timestamp >= withdrawStart, "Too early");
        require(block.timestamp < cancelStart, "Too late");
        require(keccak256(abi.encodePacked(secret)) == hashlock, "Wrong secret");

        withdrawn = true;

        ITRC20(token).transfer(taker, amount);
        payable(maker).transfer(safetyDeposit);

        emit Withdrawn(secret);
    }

    function cancel() external {
        require(!withdrawn && !cancelled, "Done");
        require(msg.sender == maker, "Not maker");
        require(block.timestamp >= cancelStart, "Too early");

        cancelled = true;

        ITRC20(token).transfer(maker, amount);
        payable(maker).transfer(safetyDeposit);

        emit Cancelled();
    }
}
