// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

/**
 * @title MinimalEscrow
 * @dev Absolute minimal escrow contract to reduce energy usage
 * @notice Native TRX only, no tokens, minimal operations
 */
contract MinimalEscrow {

    address public maker;
    address public taker;
    bytes32 public hashlock;
    uint256 public amount;
    uint256 public unlockTime;
    bool public completed;

    event EscrowCreated(address maker, address taker, uint256 amount);
    event EscrowCompleted(address taker);

    constructor(
        address _taker,
        bytes32 _hashlock,
        uint256 _delay
    ) payable {
        require(msg.value > 0, "Need TRX");
        require(_taker != address(0), "Invalid taker");

        maker = msg.sender;
        taker = _taker;
        hashlock = _hashlock;
        amount = msg.value;
        unlockTime = block.timestamp + _delay;

        emit EscrowCreated(maker, taker, amount);
    }

    function withdraw(bytes32 secret) external {
        require(!completed, "Done");
        require(msg.sender == taker, "Not taker");
        require(block.timestamp >= unlockTime, "Too early");
        require(keccak256(abi.encodePacked(secret)) == hashlock, "Wrong secret");

        completed = true;
        payable(taker).transfer(amount);

        emit EscrowCompleted(taker);
    }

    function cancel() external {
        require(!completed, "Done");
        require(msg.sender == maker, "Not maker");
        require(block.timestamp >= unlockTime + 300, "Too early to cancel");

        completed = true;
        payable(maker).transfer(amount);
    }
}

