// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

interface ITRC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title TronEscrowDebug
 * @dev Debug version of TronEscrowSimplified with better error reporting
 */
contract TronEscrowDebug {

    // Escrow states for clarity
    enum EscrowState { ACTIVE, WITHDRAWN, CANCELLED }

    // Escrow data structure
    struct EscrowData {
        address maker;           // Who locked the funds
        address taker;           // Who can unlock with secret
        address token;           // TRC-20 token address
        uint256 amount;          // Amount locked
        bytes32 hashlock;        // keccak256(secret)
        uint256 withdrawStart;   // When withdrawal becomes possible
        uint256 cancelStart;     // When cancellation becomes possible
        uint256 safetyDeposit;   // TRX safety deposit
        EscrowState state;       // Current state
    }

    // Storage
    EscrowData public escrow;

    // Constants
    uint256 public constant MINIMUM_SAFETY_DEPOSIT = 1000000; // 1 TRX in sun units

    // Events
    event EscrowCreated(
        address indexed maker,
        address indexed taker,
        address indexed token,
        uint256 amount,
        bytes32 hashlock
    );

    event DebugInfo(string message, uint256 value);

    /**
     * @dev Constructor creates and initializes the escrow
     */
    constructor(
        address _taker,
        address _token,
        uint256 _amount,
        bytes32 _hashlock,
        uint256 _withdrawDelay,
        uint256 _cancelDelay
    ) payable {
        // Debug: Log the received value
        emit DebugInfo("Received msg.value", msg.value);
        emit DebugInfo("Required minimum", MINIMUM_SAFETY_DEPOSIT);

        // Basic validation with clear error messages
        require(_taker != address(0), "Invalid taker");
        require(_token != address(0), "Invalid token");
        require(_amount > 0, "Invalid amount");
        require(_hashlock != bytes32(0), "Invalid hashlock");
        require(_cancelDelay > _withdrawDelay, "Invalid delays");

        // This is the failing check - let's be very explicit
        if (msg.value < MINIMUM_SAFETY_DEPOSIT) {
            emit DebugInfo("FAILED: msg.value", msg.value);
            emit DebugInfo("FAILED: required", MINIMUM_SAFETY_DEPOSIT);
            revert("Insufficient safety deposit - debug version");
        }

        emit DebugInfo("Safety deposit OK", msg.value);

        // Initialize escrow data
        escrow = EscrowData({
            maker: msg.sender,
            taker: _taker,
            token: _token,
            amount: _amount,
            hashlock: _hashlock,
            withdrawStart: block.timestamp + _withdrawDelay,
            cancelStart: block.timestamp + _cancelDelay,
            safetyDeposit: msg.value,
            state: EscrowState.ACTIVE
        });

        emit DebugInfo("Before token transfer", 0);

        // Transfer tokens from maker to this contract
        bool success = ITRC20(_token).transferFrom(msg.sender, address(this), _amount);
        require(success, "Token transfer failed");

        emit DebugInfo("After token transfer", 1);

        emit EscrowCreated(
            msg.sender,
            _taker,
            _token,
            _amount,
            _hashlock
        );

        emit DebugInfo("Constructor completed", block.timestamp);
    }

    /**
     * @dev Simple withdraw function for testing
     */
    function withdraw(bytes32 secret) external {
        require(escrow.state == EscrowState.ACTIVE, "Not active");
        require(msg.sender == escrow.taker, "Not taker");
        require(block.timestamp >= escrow.withdrawStart, "Too early");
        require(block.timestamp < escrow.cancelStart, "Too late");
        require(keccak256(abi.encodePacked(secret)) == escrow.hashlock, "Wrong secret");

        escrow.state = EscrowState.WITHDRAWN;

        // Transfer tokens to taker
        ITRC20(escrow.token).transfer(escrow.taker, escrow.amount);

        // Return safety deposit to maker
        payable(escrow.maker).transfer(escrow.safetyDeposit);
    }

    /**
     * @dev Cancel function
     */
    function cancel() external {
        require(escrow.state == EscrowState.ACTIVE, "Not active");
        require(msg.sender == escrow.maker, "Not maker");
        require(block.timestamp >= escrow.cancelStart, "Too early to cancel");

        escrow.state = EscrowState.CANCELLED;

        // Return tokens to maker
        ITRC20(escrow.token).transfer(escrow.maker, escrow.amount);

        // Return safety deposit to maker
        payable(escrow.maker).transfer(escrow.safetyDeposit);
    }

    // View functions for debugging
    function getEscrowInfo() external view returns (
        address maker,
        address taker,
        address token,
        uint256 amount,
        bytes32 hashlock,
        uint256 withdrawStart,
        uint256 cancelStart,
        uint256 safetyDeposit,
        EscrowState state
    ) {
        return (
            escrow.maker,
            escrow.taker,
            escrow.token,
            escrow.amount,
            escrow.hashlock,
            escrow.withdrawStart,
            escrow.cancelStart,
            escrow.safetyDeposit,
            escrow.state
        );
    }
}
