// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

interface ITRC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title TronEscrowSimplified
 * @dev Simplified HTLC escrow contract for cross-chain atomic swaps
 * @notice Based on 1inch architecture but simplified for hackathon use
 */
contract TronEscrowSimplified {

    // Escrow states for clarity
    enum EscrowState { ACTIVE, WITHDRAWN, CANCELLED }

    // Escrow data structure
    struct EscrowData {
        address maker;           // Who locked the funds
        address taker;           // Who can unlock with secret
        address token;           // TRC-20 token address
        uint256 amount;          // Amount locked
        bytes32 hashlock;        // keccak256(secret)
        uint256 withdrawStart;   // When withdrawal becomes possible
        uint256 cancelStart;     // When cancellation becomes possible
        uint256 safetyDeposit;   // TRX safety deposit
        EscrowState state;       // Current state
    }

    // Storage
    EscrowData public escrow;

    // Constants
    uint256 public constant MINIMUM_SAFETY_DEPOSIT = 1000000; // 1 TRX in sun units

    // Events matching 1inch pattern
    event EscrowCreated(
        address indexed maker,
        address indexed taker,
        address indexed token,
        uint256 amount,
        bytes32 hashlock,
        uint256 withdrawStart,
        uint256 cancelStart
    );

    event EscrowWithdrawal(
        address indexed taker,
        bytes32 secret
    );

    event EscrowCancelled(
        address indexed maker
    );

    // Custom errors for gas efficiency
    error InvalidCaller();
    error InvalidSecret();
    error InvalidTime();
    error InvalidAmount();
    error InvalidState();
    error InsufficientSafetyDeposit();
    error TokenTransferFailed();

    /**
     * @dev Constructor creates and initializes the escrow
     * @param _taker Address that can unlock the escrow with correct secret
     * @param _token TRC-20 token contract address
     * @param _amount Amount of tokens to lock
     * @param _hashlock keccak256 hash of the secret
     * @param _withdrawDelay Seconds until withdrawal is allowed (e.g., 7200 = 2 hours)
     * @param _cancelDelay Seconds until cancellation is allowed (e.g., 14400 = 4 hours)
     */
    constructor(
        address _taker,
        address _token,
        uint256 _amount,
        bytes32 _hashlock,
        uint256 _withdrawDelay,
        uint256 _cancelDelay
    ) payable {
        // Validation
        require(_taker != address(0), "Invalid taker address");
        require(_token != address(0), "Invalid token address");
        require(_amount > 0, "Amount must be greater than zero");
        require(_hashlock != bytes32(0), "Invalid hashlock");
        require(_cancelDelay > _withdrawDelay, "Cancel delay must be after withdraw delay");
        require(msg.value >= MINIMUM_SAFETY_DEPOSIT, "Insufficient safety deposit");

        // Initialize escrow data
        escrow = EscrowData({
            maker: msg.sender,
            taker: _taker,
            token: _token,
            amount: _amount,
            hashlock: _hashlock,
            withdrawStart: block.timestamp + _withdrawDelay,
            cancelStart: block.timestamp + _cancelDelay,
            safetyDeposit: msg.value,
            state: EscrowState.ACTIVE
        });

        // Transfer tokens from maker to this contract
        require(
            ITRC20(_token).transferFrom(msg.sender, address(this), _amount),
            "Token transfer failed"
        );

        emit EscrowCreated(
            msg.sender,
            _taker,
            _token,
            _amount,
            _hashlock,
            escrow.withdrawStart,
            escrow.cancelStart
        );
    }

    /**
     * @dev Taker withdraws tokens by providing the correct secret
     * @param secret The secret that unlocks the escrow
     */
    function withdraw(bytes32 secret) external {
        // Validation checks
        if (escrow.state != EscrowState.ACTIVE) revert InvalidState();
        if (msg.sender != escrow.taker) revert InvalidCaller();
        if (block.timestamp < escrow.withdrawStart) revert InvalidTime();
        if (block.timestamp >= escrow.cancelStart) revert InvalidTime();
        if (keccak256(abi.encodePacked(secret)) != escrow.hashlock) revert InvalidSecret();

        // Update state
        escrow.state = EscrowState.WITHDRAWN;

        // Transfer tokens to taker
        require(
            ITRC20(escrow.token).transfer(escrow.taker, escrow.amount),
            "Token transfer to taker failed"
        );

        // Return safety deposit to maker
        payable(escrow.maker).transfer(escrow.safetyDeposit);

        emit EscrowWithdrawal(escrow.taker, secret);
    }

    /**
     * @dev Maker cancels the escrow after the cancellation period
     */
    function cancel() external {
        // Validation checks
        if (escrow.state != EscrowState.ACTIVE) revert InvalidState();
        if (msg.sender != escrow.maker) revert InvalidCaller();
        if (block.timestamp < escrow.cancelStart) revert InvalidTime();

        // Update state
        escrow.state = EscrowState.CANCELLED;

        // Return tokens to maker
        require(
            ITRC20(escrow.token).transfer(escrow.maker, escrow.amount),
            "Token transfer to maker failed"
        );

        // Return safety deposit to maker
        payable(escrow.maker).transfer(escrow.safetyDeposit);

        emit EscrowCancelled(escrow.maker);
    }

    /*
     * @dev View function to get all escrow details
     * @return All escrow data in a single call
     */
    function getEscrowDetails() external view returns (
        address maker,
        address taker,
        address token,
        uint256 amount,
        bytes32 hashlock,
        uint256 withdrawStart,
        uint256 cancelStart,
        uint256 safetyDeposit,
        EscrowState state
    ) {
        return (
            escrow.maker,
            escrow.taker,
            escrow.token,
            escrow.amount,
            escrow.hashlock,
            escrow.withdrawStart,
            escrow.cancelStart,
            escrow.safetyDeposit,
            escrow.state
        );
    }

    /**
     * @dev Check if withdrawal is currently possible
     * @return true if withdrawal window is open
     */
    function canWithdraw() external view returns (bool) {
        return escrow.state == EscrowState.ACTIVE && 
               block.timestamp >= escrow.withdrawStart && 
               block.timestamp < escrow.cancelStart;
    }

    /**
     * @dev Check if cancellation is currently possible
     * @return true if cancellation is allowed
     */
    function canCancel() external view returns (bool) {
        return escrow.state == EscrowState.ACTIVE && 
               block.timestamp >= escrow.cancelStart;
    }
}

